    body: JSON.stringify({
        login: {
            alertListTitle: "Alerts",
            buttonText: "Continue",
            description: "Login to",
            editEmailText: "Edit",
            emailPlaceholder: "Email address",
            federatedConnectionButtonText: `Continue with ${connectionName}`,
            footerLinkText: "Sign up",
